print(sum(1 for r in range(1, len(open('input.txt').readlines())-1) for c in range(1, len(open('input.txt').readline().strip())-1) if open('input.txt').readlines()[r][c] == 'A' and (lambda tl, tr: sorted(tl) == ['M', 'S'] and sorted(tr) == ['M', 'S'])(open('input.txt').readlines()[r-1][c-1] + open('input.txt').readlines()[r+1][c+1], open('input.txt').readlines()[r-1][c+1] + open('input.txt').readlines()[r+1][c-1])))
